import React, { useRef } from "react";
import { useParams } from "react-router-dom";
import Footer from "../../components/Footer";
import Header from "../../components/Header";
import Axios from "axios";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import {
  ContainerBook,
  ContainerInfosBook,
  InputChangeImage,
  ContainerInputs,
  InputEditBookDescription,
  InputEditBook,
  ContainerButtonAdd,
  ButtonAddBook,
  Title,
} from "./styles";

const Editbook = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const title = useRef();
  const description = useRef();
  const year = useRef();
  const bookCover = useRef();

 async function editBook(e) {
    //previne reload à pagina
    e.preventDefault();
    console.log("edit book");

    const editedBook = {
      title: title.current.value,
      description: description.current.value,
      year: year.current.value,
    };

    const url = "http://localhost:3000/api/v1/book/" + id;
    const token = sessionStorage.getItem("token");

    try {
     const response = await Axios.put(url, JSON.stringify(editedBook), {
        headers: { "Content-Type": "application/json", Authorization: token },
      });

      console.log(response);
      navigate("/gallery");
    } catch (error) {
      console.log(error);
      toast.error(error.response.data.message, {
        position: toast.POSITION.TOP_RIGHT,
      });
    }
  }

  return (
    <>
      <Header />
      <ToastContainer />
      <Title>Edit Book {id}</Title>
      <ContainerBook>
        <ContainerInfosBook onSubmit={editBook}>
          <ContainerInputs>
            <InputChangeImage
              name="imageBook"
              type="text"
              placeholder="book cover url"
              id="add_book_cover"
              ref={bookCover}
            />
            <InputEditBook
              name="titleBook"
              placeholder="Book title"
              type="text"
              id="add_book_title"
              ref={title}
            />
            <InputEditBookDescription
              name="descriptionBook"
              placeholder="Book description"
              type="text"
              id="add_book_description"
              ref={description}
            />
            <InputEditBook
              name="yearBook"
              placeholder="Book year"
              type="number"
              id="add_book_number"
              ref={year}
            />
          </ContainerInputs>
          <ContainerButtonAdd>
            <ButtonAddBook>Edit</ButtonAddBook>
          </ContainerButtonAdd>
        </ContainerInfosBook>
      </ContainerBook>
      <Footer />
    </>
  );
};

export default Editbook;
