import React, { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import Header from "../../components/Header";
import Submission from "../../components/Submission";
import Axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { ContainerSubmissions } from "./styles";
import { submissionUrl,images_test} from "../../resources/constants.js";


const Gallery = () => {
//TESTING code
const [media, setMedia] = useState([]);

useEffect(() => {
  fetchMedia();
}, []);

const fetchMedia = async () => {
  try {
    const response = await Axios.get(images_test); // Replace submissionUrl with the actual API endpoint URL for getting all media
    setMedia(response.data);
  } catch (error) {
    toast.error("Failed to fetch media");
  }
};
// END TESTING code

  const [submissions, setSubmissions] = useState([]);
  const token = sessionStorage.getItem("token");
  const isLoggedIn = token !== null;


  useEffect(() => {
    getSubmissions();
  }, []);

  // Não usa token de auth
  async function getSubmissions() {
    const url = submissionUrl;
    try {
      const response = await Axios.get(url, {
        headers: { "Content-Type": "application/json" },
      });
      console.log(response.data.submissions);
      setSubmissions(response.data.submissions);
    } catch (error) {
      console.log(error);
    }
  }

  /*async function isOwner(id) {
    const url = submissionUrl + id;
    try {
