        headers: {
          Authorization: "Bearer " + token,
        },
      });
      console.log(response);
      return true
    } catch (error) {
      return false
      //console.log(error);
    }
  }*/

  //TODO: fix error layout error when no susbmissions on database
  /*
  return (
    <>
      <Header />
        <ToastContainer />
      <ContainerSubmissions>
        {submissions.map((submissions, index) => (
          <Submission
            image={submissions.image}
            body_part={submissions.body_part}
            date={submissions.date}
            description={submissions.description}
            id={submissions.id}
            refreshSubmissions={getSubmissions}
            key={index}
            owner={{}/*isOwner()}
          />
        ))}
      </ContainerSubmissions>
      <Footer />
    </>
  );
};
*/

if (!Array.isArray(media)) {
  return null; // or display an error message
}

return (
  <>
    <Header />
    <ToastContainer />
    <ContainerSubmissions>
      {/* Render the media */}
      {media.map((item) => (

         <div key={item.id}>
           {item.mediaType == "image" && (
             <img
               src={`data:image/jpeg;base64,${item.media}`}
             />
           )}
           {item.mediaType == "video" && (
             <video controls>
               <source
                 src={`data:video/mp4;base64,${item.media}`}
               />
             </video>
           )}
         </div>
      ))}
    </ContainerSubmissions>
    <Footer />
  </>
);
};
