import Axios from "axios";
import {

} from "./styles";

const Profile = () => {
  const token = sessionStorage.getItem("token");
  const isLoggedIn = token !== null;
  // Decode the token 
  const decodedToken = JSON.parse(atob(token.split(".")[1]));

  // Get the user_id from the decoded token
  const userID = decodedToken.user_id  

  const user = 
    {
      user_image: "",
      user_name: "",
      user_email: "gggggg",
    };

    getMyUser();

    async function getMyUser(){
      const url = "http://localhost:3000/api/v1/user/" + 4;
      try {
        const response = await Axios.get(url, {
          headers: { "Content-Type": "application/json", Authorization: token },
        });
        console.log(response.data.user.name);
        user.user_image=response.data.user.profile_picture
        user.user_name=response.data.user.name
        user.user_email=response.data.user.email

        //setUser(response.data.user);
        console.log(user);
        return response;
      } catch (error) {
        console.log(error);
        return error;
      }
    }


    return (
      <>
        <Header />
          {<User
          userImage={user.user_image}
          userName= {user.user_name}
          userEmail= {user.user_email}
          />}
        <Footer />
      </>
    );
  };

export default Profile;
