import React, { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import Header from "../../components/Header";
import User from "../../components/User";
import Axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";
import { userUrl } from "../../resources/constants.js";
import { Form, ClinicalList, ClinicalItem, RemoveButton } from "./styles";

const Profile = () => {
  const navigate = useNavigate();
  const token = sessionStorage.getItem("token");
  const isLoggedIn = token !== null;
  const decodedToken = JSON.parse(atob(token.split(".")[1]));
  const userID = decodedToken.user_id;

  const [user, setUser] = useState({
    user_name: "",
    user_email: "",
    clinicals: [],
  });

  const [clinical, setClinical] = useState("");

  useEffect(() => {
    getMyUser();
  }, []);

  async function getMyUser() {
    const url = userUrl + userID;
    try {
      const response = await Axios.get(url, {
        headers: { "Content-Type": "application/json", Authorization: token },
      });
      const userData = response.data.user;
      setUser({
        user_name: userData.name,
        user_email: userData.email,
        clinicals: userData.clinicals,
      });
    } catch (err) {
      console.log(err);
      toast.error(err.response.data.message, {
        position: toast.POSITION.TOP_RIGHT,
      });
      if (err.response.data.message === "token is not valid") {
        navigate("/login");
      }
    }
  }

  async function addClinical() {
    const url = userUrl;
    try {
      const response = await Axios.post(
        url,
        { clinical: clinical },
        {
          headers: { "Content-Type": "application/json", Authorization: token },
        }
      );
      const updatedClinicals = [...user.clinicals, response.data.clinical];
      setUser((prevState) => ({
        ...prevState,
        clinicals: updatedClinicals,
      }));
      toast.success("Clinical added successfully", {
        position: toast.POSITION.TOP_RIGHT,
      });
      setClinical(""); // Clear the input field after adding clinical
    } catch (err) {
      console.log(err);
      toast.error(err.response.data.message, {
        position: toast.POSITION.TOP_RIGHT,
      });
    }
  }

  async function removeClinical(clinicalID) {
    const url = userUrl + clinicalID;
    try {
      await Axios.delete(url, {
        headers: { "Content-Type": "application/json", Authorization: token },
      });
      const updatedClinicals = user.clinicals.filter(
        (clinical) => clinical.id !== clinicalID
      );
      setUser((prevState) => ({
        ...prevState,
        clinicals: updatedClinicals,
      }));
      toast.success("Clinical removed successfully", {
        position: toast.POSITION.TOP_RIGHT,
      });
    } catch (err) {
      console.log(err);
      toast.error(err.response.data.message, {
        position: toast.POSITION.TOP_RIGHT,
      });
    }
  }

  return (
    <>
      <Header />
      <ToastContainer />
      {isLoggedIn && (
        <User userName={user.user_name} userEmail={user.user_email} />
      )}
      <Form onSubmit={addClinical}>
        <label>Add Clinicals to Evaluate:</label>
        <input
          type="text"
          name="clinical"
          value={clinical}
          onChange={(e) => setClinical(e.target.value)}
        />
        <button type="submit">Add</button>
      </Form>
      <ClinicalList>
  {user.clinicals && user.clinicals.length !== 0 && (
    user.clinicals.map((clinical) => (
      <ClinicalItem key={clinical.id}>
        {clinical.email}
        <RemoveButton onClick={() => removeClinical(clinical.id)}>
          X
        </RemoveButton>
      </ClinicalItem>
    ))
  )}
</ClinicalList>

      <Footer />
    </>
  );
};

export default Profile;
