import React, { useRef } from "react";
import Footer from "../../components/Footer";
import Header from "../../components/Header";
import Axios from "axios";
import { useNavigate } from "react-router-dom";

import {
  ContainerBook,
  ContainerInfosBook,
  InputChangeImage,
  ContainerInputs,
  InputEditBookDescription,
  InputEditBook,
  ContainerButtonAdd,
  ButtonAddBook,
} from "./styles";

/*
book_cover: "https://picsum.photos/id/237/200/300",
book_cover: "https://picsum.photos/id/238/200/300",
book_cover: "https://picsum.photos/id/239/200/300",
book_cover: "https://picsum.photos/id/240/200/300",
book_cover: "https://picsum.photos/id/241/200/300",
*/

const Addbook = () => {
  const navigate = useNavigate();

  const title = useRef();
  const description = useRef();
  const year = useRef();
  const bookCover = useRef();

  async function addBook(e) {
    e.preventDefault();
    console.log("add book");

    const newBook = {
      title: title.current.value,
      description: description.current.value,
      year: year.current.value,
    };
    if (bookCover.current.value !== "") {
      newBook.book_cover = bookCover.current.value;
    }
    const url = "http://localhost:3000/api/v1/book/";
    const token = sessionStorage.getItem("token");

    try {
     const response = await Axios.post(url, JSON.stringify(newBook), {
        headers: { "Content-Type": "application/json", Authorization: token },
      });

      console.log(response);
      navigate("/gallery");
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <>
      <Header />
      <ContainerBook>
        <ContainerInfosBook onSubmit={addBook}>
          <ContainerInputs>
            <InputChangeImage
              name="imageBook"
              type="text"
              placeholder="book cover url"
              id="add_book_cover"
              ref={bookCover}
            />
            <InputEditBook
              name="titleBook"
              placeholder="Book title"
              type="text"
              id="add_book_title"
              required
              ref={title}
            />
            <InputEditBookDescription
              name="descriptionBook"
              placeholder="Book description"
              type="text"
              id="add_book_description"
              required
              ref={description}
            />
            <InputEditBook
              name="yearBook"
              placeholder="Book year"
              type="number"
              id="add_book_number"
              required
              ref={year}
            />
          </ContainerInputs>
          <ContainerButtonAdd>
            <ButtonAddBook>Add</ButtonAddBook>
          </ContainerButtonAdd>
        </ContainerInfosBook>
      </ContainerBook>
      <Footer />
    </>
  );
};

export default Addbook;
