import {
  ContainerBook,
  ContainerImage,
  ImageBook,
  TitleBook,
  Button,
  ButtonContainer,
} from "./styles";
/*
properties que o elemento recebe
*/
const Book = ({ image, title, showInfo }) => {
  const isLoggedIn = false;

  function deleteBook(element) {
    console.log("delete book");
    console.log(element.target);
    element.target.parentNode.parentNode.remove();
  }
  function editBook(element) {
    console.log("edit book");
    console.log(element.target);

  }
  return (
    <>
      <ContainerBook>
        <TitleBook>{title}</TitleBook>
        <ContainerImage>
          <ImageBook src={image} />
        </ContainerImage>
        <center>
          {showInfo}
          {isLoggedIn && (
            <ButtonContainer>
              <Button type="button" onClick={editBook}>
              Update
            </Button>
            <Button type="button" onClick={deleteBook}>
              Delete
            </Button>
            </ButtonContainer>
          )}
        </center>
      </ContainerBook>
    </>
  );
};

export default Book;
