import React, { useRef } from "react";
import Footer from "../../components/Footer";
import Header from "../../components/Header";
import Axios from "axios";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import {
  ContainerSubmission,
  ContainerInfosSubmission,
  InputChangeImage,
  ContainerInputs,
  InputEditSubmissionDescription,
  InputEditSubmission,
  ContainerButtonAdd,
  ButtonAddSubmission,
} from "./styles";
import { submissionUrl } from "../../resources/constants.js";

const AddSubmission = () => {
  const navigate = useNavigate();

  const body_part = useRef();
  const description = useRef();
  const date = useRef();
  const image = useRef();


  async function addSubmission(e) {
    e.preventDefault();
    console.log("add submission");

  /*  BodyPart string `json:"body_part" binding:"required"`
    Image       []byte `json:"image" binding:"required"`
    Date        string `json:"date" binding:"required"`
    ID          uint64 `json:"id" binding:"required"`
    Description string `json:"description" binding:"required"`
*/
    const newSubmission = {
      body_part: body_part.current.value,
      description: description.current.value,
      date: date.current.value,
      image: image.current.value,
    };

    if (body_part.current.value !== "") {
      newSubmission.submission_cover = "Unknown";


    const file = image.current.files[0];

    if (file) {
      const formData = new FormData();
      formData.append("file", file);

      newSubmission.file = formData;
    }

    }
    const url = submissionUrl;
    const token = sessionStorage.getItem("token");

    try {
     const response = await Axios.post(url, JSON.stringify(newSubmission), {
        headers: { "Content-Type": "application/json", Authorization: token },
      });

      console.log(response);
      navigate("/gallery");
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <>
      <Header />
      <ToastContainer />
      <ContainerSubmission>
        <ContainerInfosSubmission onSubmit={addSubmission}>
          <ContainerInputs>
            <InputEditSubmissionDescription
              name="descriptionSubmission"
              placeholder="Submission description"
              type="text"
              id="add_submission_description"
              required
              ref={description}
            />
            <InputEditSubmission
              name="yearSubmission"
              placeholder="Submission date"
              type="number"
              id="add_submission_number"
              required
              ref={date}
            />
            {/* Add a file input for uploading images or videos */}
            <input
              type="file"
              name="file"
              accept="image/*, video/*"
              ref={image}
            />
            <select
              name="bodyPartSubmission"
              id="add_submission_body_part"
              required
              ref={body_part}
            >
              <option value="">Select Body Part</option>
              <option value="Face">Face</option>
              <option value="Head">Head</option>
              <option value="Hand">Hand</option>
              <option value="Feet">Feet</option>
              <option value="Leg">Leg</option>
              <option value="Body">Body</option>
              <option value="Chest">Chest</option>
              <option value="Back">Back</option>
              <option value="Arm">Arm</option>
              <option value="Belly">Belly</option>
            </select>
          </ContainerInputs>
          <ContainerButtonAdd>
            <ButtonAddSubmission>Add</ButtonAddSubmission>
          </ContainerButtonAdd>
        </ContainerInfosSubmission>
      </ContainerSubmission>
      <Footer />
    </>
  );
};

export default AddSubmission;
