import React from "react";
import { useNavigate } from "react-router-dom";
import {
  ContainerSubmission,
  ContainerImage,
  ImageSubmission,
  TitleSubmission,
  Button,
  ButtonContainer,
  DateText,
} from "./styles";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Axios from "axios";
import { submissionUrl } from "../../resources/constants.js";


const Submission = ({ image, w, date, description, id, refreshSubmissions, owner }) => {
  const token = sessionStorage.getItem("token");
  const isLoggedIn = token !== null;
  // variavel usada pra fazer a navegação pelas paginas
  const navigate = useNavigate();

  async function deleteSubmission(element) {
    console.log("delete Submission" + id);
    // console.log(element.target);
    // element.target.parentNode.parentNode.remove();

    const url = submissionUrl + id;
    try {
      await Axios.delete(url, {
        headers: {
          Authorization: "Bearer " + token,
        },
      });
      refreshSubmissions(" from delete");
    } catch (err) {
      console.log(err);
      toast.error(err.response.data.message, {
        position: toast.POSITION.TOP_RIGHT,
      });
    }
  }
  /*
  First we perform a get to know if the user is the ownner of the Submission and have the right to edit it
  */
  async function editSubmission(element) {
    console.log("edit Submission button");

    const url = submissionUrl + id;
    try {
      await Axios.get(url, {
        headers: {
          Authorization: "Bearer " + token,
        },
      });
      console.log(element.target);
      navigate(`/editsubmission/${id}`);
    } catch (err) {
      console.log(err);
      toast.error(err.response.data.message, {
        position: toast.POSITION.TOP_RIGHT,
      });
    if (err.response.data.message == "token is not valid") {
       navigate("/login");
      }
    }

  }
 /* return (
    <>
     <ToastContainer />
      <ContainerSubmission>
        <TitleSubmission>{bodyPart}</TitleSubmission>
        <ContainerImage>
          <ImageSubmission src={image} />
        </ContainerImage>
        <center>
          {description}
          {isLoggedIn && owner && (
            <ButtonContainer>
              <Button type="button" onClick={editSubmission}>
              Update
            </Button>
            <Button type="button" onClick={deleteSubmission}>
              Delete
            </Button>
            </ButtonContainer>
          )}
        </center>
      </ContainerSubmission>
    </>
  );*/
  return (
    <>
      <ToastContainer />
      <ContainerSubmission>
        <TitleSubmission>{bodyPart}</TitleSubmission>
        <ContainerImage>
          <ImageSubmission src={image} alt={bodyPart} />
        </ContainerImage>
        <p>{description}</p>
        <DateText>{date}</DateText>
        <center>
          {owner && (
            <ButtonContainer>
              <Button type="button" onClick={editSubmission}>
                Update
              </Button>
              <Button type="button" onClick={deleteSubmission}>
                Delete
              </Button>
            </ButtonContainer>
          )}
        </center>
      </ContainerSubmission>
    </>
  );
};


export default Submission;
