import React, { useRef } from "react";
import Footer from "../../components/Footer";
import Header from "../../components/Header";
import Axios from "axios";
import { useNavigate } from "react-router-dom";

import {
  ContainerSubmission,
  ContainerInfosSubmission,
  InputChangeImage,
  ContainerInputs,
  InputEditSubmissionDescription,
  InputEditSubmission,
  ContainerButtonAdd,
  ButtonAddSubmission,
} from "./styles";
import { submissionUrl } from "../../resources/constants.js";

const AddSubmission = () => {
  const navigate = useNavigate();

  const title = useRef();
  const description = useRef();
  const year = useRef();
  const submissionCover = useRef();

  async function addSubmission(e) {
    e.preventDefault();
    console.log("add submission");

    const newSubmission = {
      title: title.current.value,
      description: description.current.value,
      year: year.current.value,
    };
    if (submissionCover.current.value !== "") {
      newSubmission.submission_cover = submissionCover.current.value;
    }
    const url = submissionUrl;
    const token = sessionStorage.getItem("token");

    try {
     const response = await Axios.post(url, JSON.stringify(newSubmission), {
        headers: { "Content-Type": "application/json", Authorization: token },
      });

      console.log(response);
      navigate("/gallery");
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <>
      <Header />
      <ContainerSubmission>
        <ContainerInfosSubmission onSubmit={addSubmission}>
          <ContainerInputs>
            <InputChangeImage
              name="imageSubmission"
              type="text"
              placeholder="Submission cover url"
              id="add_submission_cover"
              ref={submissionCover}
            />
            <InputEditSubmission
              name="titleSubmission"
              placeholder="Submission title"
              type="text"
              id="add_submission_title"
              required
              ref={title}
            />
            <InputEditSubmissionDescription
              name="descriptionSubmission"
              placeholder="Submission description"
              type="text"
              id="add_submission_description"
              required
              ref={description}
            />
            <InputEditSubmission
              name="yearSubmission"
              placeholder="Submission year"
              type="number"
              id="add_submission_number"
              required
              ref={year}
            />
          </ContainerInputs>
          <ContainerButtonAdd>
            <ButtonAddSubmission>Add</ButtonAddSubmission>
          </ContainerButtonAdd>
        </ContainerInfosSubmission>
      </ContainerSubmission>
      <Footer />
    </>
  );
};

export default AddSubmission;
