import React from "react";
import { useNavigate } from "react-router-dom";
import {
  ContainerBook,
  ContainerImage,
  ImageBook,
  TitleBook,
  Button,
  ButtonContainer,
} from "./styles";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Axios from "axios";

/*
properties que o elemento recebe
*/
const Book = ({ image, title, showInfo, id, refreshBooks }) => {
  const token = sessionStorage.getItem("token");
  const isLoggedIn = token !== null;
  // variavel usada pra fazer a navegação pelas paginas
  const navigate = useNavigate();

  async function deleteBook(element) {
    console.log("delete book" + id);
    // console.log(element.target);
    // element.target.parentNode.parentNode.remove();

    const url = "http://localhost:3000/api/v1/book/" + id;
    try {
      await Axios.delete(url, {
        headers: {
          Authorization: "Bearer " + token,
        },
      });
      refreshBooks(" from delete"); // getbooks from gallery
    } catch (err) {
      console.log(err);
      toast.error(err.response.data.message, {
        position: toast.POSITION.TOP_RIGHT,
      });


    }
  }
  function editBook(element) {
    console.log("edit book button");
    console.log(element.target);
    navigate("/editbook");
  }
  return (
    <>
     <ToastContainer />
      <ContainerBook>
        <TitleBook>{title}</TitleBook>
        <ContainerImage>
          <ImageBook src={image} />
        </ContainerImage>
        <center>
          {showInfo}
          {isLoggedIn && (
            <ButtonContainer>
              <Button type="button" onClick={editBook}>
              Update
            </Button>
            <Button type="button" onClick={deleteBook}>
              Delete
            </Button>
            </ButtonContainer>
          )}
        </center>
      </ContainerBook>
    </>
  );
};

export default Book;
