import React, { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import Header from "../../components/Header";
import Book from "../../components/Book";
import Axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { ContainerBooks } from "./styles";
const Gallery = () => {
  const [books, setBooks] = useState([]);
  const token = sessionStorage.getItem("token");
  const isLoggedIn = token !== null;

  useEffect(() => {
    getBooks();
  }, []);

  // Não usa token de auth
  async function getBooks() {
    const url = "http://localhost:3000/api/v1/book/";
    try {
      const response = await Axios.get(url, {
        headers: { "Content-Type": "application/json" },
      });
      console.log(response.data.books);
      setBooks(response.data.books);
    } catch (error) {
      console.log(error);
    }
  }

  async function isOwner(id) {
    const url = "http://localhost:3000/api/v1/book/" + id;
    try {
      const response = await Axios.get(url, {
        headers: {
          Authorization: "Bearer " + token,
        },
      });
      console.log(response);
      return true
    } catch (error) {
      console.log(error);
      return false
    }
  }

  return (
    <>
      <Header />
        <ToastContainer />
      <ContainerBooks>
        {books.map((book, index) => (
          <Book
            image={book.book_cover}
            title={book.title}
            showInfo={book.description}
            id={book.id}
            refreshBooks={getBooks}
            key={index}
            owner={isOwner(book.id)}
          />
        ))}
        {/* <Book
          image={books[1].book_cover}
          title={books[1].title}
          showInfo={books[1].description}
        />
        <Book
          image={books[2].book_cover}
          title={books[2].title}
          showInfo={books[2].description}
        /> */}
      </ContainerBooks>
      <Footer />
    </>
  );
};

export default Gallery;
